import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [designerGoal, setDesignerGoal] = useState("");
  const [userProfiles, setUserProfiles] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPrompt, setCustomPrompt] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [designGuide, setDesignGuide] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);
  const [lastDocId, setLastDocId] = useState(null);

  const addUserProfile = () => {
    setUserProfiles([...userProfiles, { dimension: "", content: "" }]);
  };

  const updateUserProfile = (index, field, value) => {
    const updatedProfiles = [...userProfiles];
    updatedProfiles[index][field] = value;
    setUserProfiles(updatedProfiles);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(designGuide);
  };

  const callGPT4oAPI = async () => {
    if (!apiKey) {
      setDesignGuide("Please provide OpenAI API Key.");
      return;
    }

    setLoading(true);
    const userProfileText = userProfiles
      .map((profile, index) => `Dimension ${index + 1}: ${profile.dimension}\nContent: ${profile.content}`)
      .join("\n\n");

    const fullPrompt = `AI Designer Goal: ${designerGoal}\n\nUser Profiles:\n${userProfileText}\n\nCustom Prompt:\n${customPrompt}\n\nPlease generate a design guide based on the above information.`;

    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-4o",
          messages: [{ role: "system", content: fullPrompt }],
          max_tokens: 2000,
          temperature: 0.7,
          top_p: 1,
          frequency_penalty: 0.2,
          presence_penalty: 0.3
        })
      });

      const data = await response.json();
      const outputText = data.choices[0]?.message?.content || "Generation failed, please check API request.";
      setDesignGuide(outputText);

      const docRef = await addDoc(collection(db, "gpt_interactions"), {
        timestamp: new Date().toISOString(),
        input: fullPrompt,
        output: outputText,
        notes: ""
      });

      setLastDocId(docRef.id);
    } catch (error) {
      console.error("❌ API 请求错误:", error);
      setDesignGuide("API request failed, please check network or API Key.");
    } finally {
      setLoading(false);
    }
  };

  const saveNotes = async () => {
    if (!lastDocId) {
      alert("❌ 请先生成 Design Guide 才能添加备注！");
      return;
    }
    try {
      await updateDoc(doc(db, "gpt_interactions", lastDocId), {
        notes: notes
      });

      alert("✅ 备注已存入数据库！");
    } catch (error) {
      console.error("❌ 备注存入失败:", error);
      alert("存入失败，请检查数据库连接！");
    }
  };

  return (
    <div className="app-container">
      <h1 className="page-title">Human-AI Collaborative Method for Future-Oriented Design Prompt System</h1>
      <div className="container">
        <div className="left-section">
          <h2>Designer Goal</h2>
          <textarea
            placeholder="Enter AI designer goal definition..."
            value={designerGoal}
            onChange={(e) => setDesignerGoal(e.target.value)}
          />
          <h2>User Profile (Multiple Dimensions)</h2>
          {userProfiles.map((profile, index) => (
            <div key={index} className="user-profile-container">
              <input
                className="user-dimension"
                value={profile.dimension}
                disabled
              />
              <textarea
                className="user-content"
                placeholder={`Enter ${profile.dimension}...`}
                value={profile.content}
                onChange={(e) => updateUserProfile(index, "content", e.target.value)}
              />
            </div>
          ))}
          <button className="small-button" onClick={addUserProfile}>Add Dimension</button>
          <h2>Custom Prompt</h2>
          <textarea
            placeholder="Enter custom Prompt..."
            value={customPrompt}
            onChange={(e) => setCustomPrompt(e.target.value)}
          />
          <h2>OpenAI API Key</h2>
          <input
            type="password"
            placeholder="Enter your OpenAI API Key..."
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
          />
          <button className="full-width generate-button" onClick={callGPT4oAPI} disabled={loading}>
            {loading ? "Generating..." : "Generate Design Guide"}
          </button>
        </div>

        <div className="right-section expanded">
          <h2 className="guide-header">
            Design Guide <span className="copy-text" onClick={copyToClipboard}>Copy</span>
          </h2>
          <textarea className="design-guide-box"
            readOnly
            value={designGuide}
            placeholder="The design guide will be generated here..."
          />

          <h2 className="notes-header">
            Notes <span className="copy-text" onClick={saveNotes}>Save</span>
          </h2>
          <textarea
            className="notes-box"
            placeholder="Enter your notes here..."
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
          />
        </div>
      </div>

      {loading && (
        <div className="loading-bar">
          <div className="loading-progress"></div>
        </div>
      )}

      <style>
        {`
          body {
            font-family: Arial, sans-serif;
            background: #121212;
            color: #ffffff;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
          }
          .page-title {
            text-align: center;
            font-size: 18px;
            margin-top: 10px;
            color: #bb86fc;
          }
          .container {
            display: flex;
            width: 90vw;
            height: 90vh;
            background: #1e1e1e;
            border-radius: 10px;
            overflow: hidden;
          }
          .left-section, .right-section.expanded {
            width: 50%;
            display: flex;
            flex-direction: column;
            padding: 20px;
          }
          .user-profile-container {
            display: flex;
            flex-direction: column;
            gap: 5px;
          }
          .user-dimension {
            height: 30px;
            background: #555;
            color: #fff;
            border: none;
          }
          .design-guide-box, .notes-box {
            min-height: 250px;
          }
          .generate-button, .small-button {
            background: #bb86fc;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
          }
          .copy-text {
            color: #ffffff;
            cursor: pointer;
          }
          h2 {
            color: #bb86fc;
          }
          textarea, input {
            width: 100%;
            padding: 10px;
            border: none;
            border-radius: 5px;
            background: #333;
            color: #fff;
          }
        `}
      </style>
    </div>
  );
}

export default App;
