import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [activeGroup, setActiveGroup] = useState("A");
  const [designerGoal, setDesignerGoal] = useState("");
  const [userProfiles, setUserProfiles] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPrompt, setCustomPrompt] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [designGuide, setDesignGuide] = useState("");
  const [expertNotes, setExpertNotes] = useState("");
  const [userFeedback, setUserFeedback] = useState("");
  const [addedDimensions, setAddedDimensions] = useState("");
  const [loading, setLoading] = useState(false);
  const [lastDocId, setLastDocId] = useState(null);

  const addUserProfile = () => {
    setUserProfiles([...userProfiles, { dimension: "", content: "" }]);
  };

  const updateUserProfile = (index, field, value) => {
    const updatedProfiles = [...userProfiles];
    updatedProfiles[index][field] = value;
    setUserProfiles(updatedProfiles);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(designGuide);
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <button className={activeGroup === "A" ? "active" : ""} onClick={() => setActiveGroup("A")}>
          Group A (With Expert Designers)
        </button>
        <button className={activeGroup === "B" ? "active" : ""} onClick={() => setActiveGroup("B")}>
          Group B (Without Expert Designers)
        </button>
      </div>

      <div className="main-container">
        <h1 className="page-title">Human-AI Collaborative Method for Future-Oriented Design Prompt System</h1>
        <div className="container">
          {/* Left Section */}
          <div className="left-section">
            <h2>Designer Goal</h2>
            <textarea placeholder="Enter AI designer goal definition..." value={designerGoal} onChange={(e) => setDesignerGoal(e.target.value)} />
            <h2>User Profile (Multiple Dimensions)</h2>
            {userProfiles.map((profile, index) => (
              <div key={index} className="user-profile-container">
                <input className="user-dimension" value={profile.dimension} readOnly />
                <textarea className="user-content" placeholder={`Enter ${profile.dimension}...`} value={profile.content} onChange={(e) => updateUserProfile(index, "content", e.target.value)} />
              </div>
            ))}
            <button className="small-button" onClick={addUserProfile}>Add Dimension</button>
            <h2>Custom Prompt</h2>
            <textarea placeholder="Enter custom Prompt..." value={customPrompt} onChange={(e) => setCustomPrompt(e.target.value)} />
            <h2>OpenAI API Key</h2>
            <input type="password" placeholder="Enter your OpenAI API Key..." value={apiKey} onChange={(e) => setApiKey(e.target.value)} />
            <button className="full-width generate-button">Generate Design Guide</button>
          </div>

          {/* Right Section */}
          <div className="right-section expanded">
            <h2 className="guide-header">Design Guide <button className="copy-button" onClick={copyToClipboard}>Copy</button></h2>
            <textarea className="design-guide-box" readOnly value={designGuide} placeholder="The design guide will be generated here..." />

            {activeGroup === "A" ? (
              <>
                <h2 className="notes-header">Expert Designer Notes <button className="copy-button">Save</button></h2>
                <textarea className="notes-box" placeholder="Enter expert notes here..." value={expertNotes} onChange={(e) => setExpertNotes(e.target.value)} />
              </>
            ) : (
              <>
                <h2 className="notes-header">User Feedback <button className="copy-button">Save</button></h2>
                <textarea className="notes-box" placeholder="What aspects are you dissatisfied with?" value={userFeedback} onChange={(e) => setUserFeedback(e.target.value)} />

                <h2 className="notes-header">Additional Dimensions <button className="copy-button">Generate</button></h2>
                <textarea className="notes-box" readOnly placeholder="LLM will generate additional dimensions here..." value={addedDimensions} />
              </>
            )}
          </div>
        </div>
      </div>

      <style>
        {`
          body, html {
            height: 100%;
            margin: 0;
            font-family: Arial, sans-serif;
            background: #121212;
            color: white;
            display: flex;
          }
          .sidebar {
            width: 15%;
            background: #1e1e1e;
            display: flex;
            flex-direction: column;
            padding: 10px;
          }
          .sidebar button {
            background: #333;
            color: white;
            padding: 10px;
            border: none;
            margin-bottom: 10px;
            cursor: pointer;
          }
          .sidebar button.active {
            background: #bb86fc;
          }
          .main-container {
            width: 85%;
            display: flex;
            flex-direction: column;
            padding: 20px;
          }
          .container {
            display: flex;
            width: 100%;
          }
          .left-section, .right-section {
            width: 50%;
            padding: 20px;
          }
          .generate-button, .small-button, .copy-button {
            background: #bb86fc;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            padding: 8px 12px;
          }
          .design-guide-box, .notes-box {
            min-height: 100px;
          }
          h2 {
            color: #bb86fc;
          }
          textarea, input {
            width: 100%;
            padding: 10px;
            border: none;
            border-radius: 5px;
            background: #333;
            color: white;
          }
        `}
      </style>
    </div>
  );
}

export default App;
