import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [group, setGroup] = useState("A"); // "A" for Group A, "B" for Group B
  const [designerGoal, setDesignerGoal] = useState("");
  const [userProfiles, setUserProfiles] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPrompt, setCustomPrompt] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [designGuide, setDesignGuide] = useState("");
  const [notes, setNotes] = useState("");
  const [userFeedback, setUserFeedback] = useState(""); // B Group: User feedback
  const [additionalDimensions, setAdditionalDimensions] = useState(""); // B Group: LLMs suggested dimensions
  const [loading, setLoading] = useState(false);
  const [lastDocId, setLastDocId] = useState(null);

  const addUserProfile = () => {
    setUserProfiles([...userProfiles, { dimension: "", content: "" }]);
  };

  const updateUserProfile = (index, field, value) => {
    const updatedProfiles = [...userProfiles];
    updatedProfiles[index][field] = value;
    setUserProfiles(updatedProfiles);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(designGuide);
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <button className={group === "A" ? "active" : ""} onClick={() => setGroup("A")}>
          Group A (With Expert Designers)
        </button>
        <button className={group === "B" ? "active" : ""} onClick={() => setGroup("B")}>
          Group B (Without Expert Designers)
        </button>
      </div>

      <div className="container">
        <div className="left-section">
          <h2>Designer Goal</h2>
          <textarea
            placeholder="Enter AI designer goal definition..."
            value={designerGoal}
            onChange={(e) => setDesignerGoal(e.target.value)}
          />
          <h2>User Profile (Multiple Dimensions)</h2>
          {userProfiles.map((profile, index) => (
            <div key={index} className="user-profile-container">
              <input
                className="user-dimension"
                value={profile.dimension}
                onChange={(e) => updateUserProfile(index, "dimension", e.target.value)}
              />
              <textarea
                className="user-content"
                placeholder={`Enter ${profile.dimension}...`}
                value={profile.content}
                onChange={(e) => updateUserProfile(index, "content", e.target.value)}
              />
            </div>
          ))}
          <button className="small-button" onClick={addUserProfile}>Add Dimension</button>
          <h2>Custom Prompt</h2>
          <textarea
            placeholder="Enter custom Prompt..."
            value={customPrompt}
            onChange={(e) => setCustomPrompt(e.target.value)}
          />
          <h2>OpenAI API Key</h2>
          <input
            type="password"
            placeholder="Enter your OpenAI API Key..."
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
          />
          <button className="full-width generate-button">Generate Design Guide</button>
        </div>

        <div className="right-section expanded">
          <h2 className="guide-header">
            Design Guide <button className="copy-button" onClick={copyToClipboard}>Copy</button>
          </h2>
          <textarea className="design-guide-box" readOnly value={designGuide} placeholder="The design guide will be generated here..." />

          {group === "A" ? (
            <>
              <h2 className="notes-header">Expert Designer Notes <button className="copy-button">Save</button></h2>
              <textarea className="notes-box" placeholder="Enter expert designer notes..." value={notes} onChange={(e) => setNotes(e.target.value)} />
            </>
          ) : (
            <>
              <h2 className="notes-header">User Feedback <button className="copy-button">Save</button></h2>
              <textarea className="notes-box" placeholder="Enter user feedback..." value={userFeedback} onChange={(e) => setUserFeedback(e.target.value)} />
              <h2 className="notes-header">Additional Dimensions <button className="copy-button">Generate</button></h2>
              <textarea className="notes-box" readOnly placeholder="LLMs will generate additional dimensions..." value={additionalDimensions} />
            </>
          )}
        </div>
      </div>

      <style>
        {`
          html, body {
            height: 100%;
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
            background: #121212;
            color: #ffffff;
            display: flex;
            justify-content: center;
            align-items: center;
          }

          .sidebar {
            position: fixed;
            left: 10px;
            top: 20px;
            display: flex;
            flex-direction: column;
            gap: 10px;
          }
          .sidebar button {
            background: #bb86fc;
            color: white;
            border: none;
            padding: 8px 12px;
            cursor: pointer;
            border-radius: 5px;
          }
          .sidebar .active {
            background: #3700b3;
          }

          .container {
            display: flex;
            width: 90vw;
            height: 90vh;
            background: #1e1e1e;
            border-radius: 10px;
            overflow: hidden;
          }

          .left-section, .right-section.expanded {
            width: 50%;
            display: flex;
            flex-direction: column;
            padding: 20px;
          }

          .copy-button, .generate-button, .small-button {
            background: #bb86fc;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            padding: 8px 12px;
          }
        `}
      </style>
    </div>
  );
}

export default App;
