import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  // State for active group selection
  const [activeGroup, setActiveGroup] = useState("A");

  // States for Group A
  const [designerGoalA, setDesignerGoalA] = useState("");
  const [userProfilesA, setUserProfilesA] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptA, setCustomPromptA] = useState("");
  const [apiKeyA, setApiKeyA] = useState("");
  const [designGuideA, setDesignGuideA] = useState("");
  const [designerNotes, setDesignerNotes] = useState("");
  const [loadingA, setLoadingA] = useState(false);
  const [lastDocIdA, setLastDocIdA] = useState(null);

  // States for Group B
  const [designerGoalB, setDesignerGoalB] = useState("");
  const [userProfilesB, setUserProfilesB] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptB, setCustomPromptB] = useState("");
  const [apiKeyB, setApiKeyB] = useState("");
  const [designGuideB, setDesignGuideB] = useState("");
  const [userFeedback, setUserFeedback] = useState("");
  const [suggestedDimensions, setSuggestedDimensions] = useState("");
  const [loadingB, setLoadingB] = useState(false);
  const [lastDocIdB, setLastDocIdB] = useState(null);

  // Common functions
  const addUserProfile = (group) => {
    if (group === "A") {
      setUserProfilesA([...userProfilesA, { dimension: "", content: "" }]);
    } else {
      setUserProfilesB([...userProfilesB, { dimension: "", content: "" }]);
    }
  };

  const updateUserProfile = (group, index, field, value) => {
    if (group === "A") {
      const updatedProfiles = [...userProfilesA];
      updatedProfiles[index][field] = value;
      setUserProfilesA(updatedProfiles);
    } else {
      const updatedProfiles = [...userProfilesB];
      updatedProfiles[index][field] = value;
      setUserProfilesB(updatedProfiles);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  // 新增生成函数
  const handleGenerateA = async () => {
    if (!apiKeyA) return;

    setLoadingA(true);
    try {
      const prompt = `Design Goal: ${designerGoalA}\nUser Profiles: ${JSON.stringify(userProfilesA)}\nCustom Prompt: ${customPromptA}`;

      setTimeout(() => {
        setDesignGuideA("Generated design guide for Group A:\n" + prompt);
        setLoadingA(false);

        const storeData = async () => {
          const docRef = await addDoc(collection(db, 'groupA'), {
            prompt,
            result: "Generated design guide for Group A",
            timestamp: new Date()
          });
          setLastDocIdA(docRef.id);
        };
        storeData();
      }, 1500);
    } catch (error) {
      console.error("生成失败:", error);
      setLoadingA(false);
    }
  };

  const handleGenerateB = async () => {
    if (!apiKeyB) return;

    setLoadingB(true);
    try {
      const prompt = `Design Goal: ${designerGoalB}\nUser Profiles: ${JSON.stringify(userProfilesB)}\nCustom Prompt: ${customPromptB}`;

      setTimeout(() => {
        setDesignGuideB("Generated design guide for Group B:\n" + prompt);
        setLoadingB(false);

        const storeData = async () => {
          const docRef = await addDoc(collection(db, 'groupB'), {
