  };

  // Function to generate suggested dimensions
  const generateSuggestedDimensions = async () => {
    if (!userFeedback.trim()) return;

    setLoadingB(true);
    try {
      setTimeout(() => {
        const mockResponse = userFeedback.includes("环保材料") 
          ? "对环保的态度\n对环保材料的认知" 
          : "用户体验期望\n功能优先级偏好";

        setSuggestedDimensions(mockResponse);
        setLoadingB(false);
      }, 1500);
    } catch (error) {
      console.error("Error generating dimensions:", error);
      setLoadingB(false);
    }
  };

