import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [currentGroup, setCurrentGroup] = useState("A"); // "A" or "B"
  const [designerGoal, setDesignerGoal] = useState("");
  const [userProfiles, setUserProfiles] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPrompt, setCustomPrompt] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [designGuide, setDesignGuide] = useState("");
  const [notes, setNotes] = useState("");
  const [userFeedback, setUserFeedback] = useState("");
  const [generatedDimension, setGeneratedDimension] = useState("");
  const [loading, setLoading] = useState(false);
  const [lastDocId, setLastDocId] = useState(null);

  const addUserProfile = () => {
    setUserProfiles([...userProfiles, { dimension: "", content: "" }]);
  };

  const updateUserProfile = (index, field, value) => {
    const updatedProfiles = [...userProfiles];
    updatedProfiles[index][field] = value;
    setUserProfiles(updatedProfiles);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(designGuide);
  };

  const callGPT4oAPI = async () => {
    if (!apiKey) {
      setDesignGuide("Please provide OpenAI API Key.");
      return;
    }

    setLoading(true);
    const userProfileText = userProfiles
      .map((profile, index) => `Dimension ${index + 1}: ${profile.dimension}\nContent: ${profile.content}`)
      .join("\n\n");

    const fullPrompt = `AI Designer Goal: ${designerGoal}\n\nUser Profiles:\n${userProfileText}\n\nCustom Prompt:\n${customPrompt}\n\nPlease generate a design guide based on the above information.`;

    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-4o",
          messages: [{ role: "system", content: fullPrompt }],
          max_tokens: 2000
        })
      });

      const data = await response.json();
      setDesignGuide(data.choices[0]?.message?.content || "Generation failed.");

      const docRef = await addDoc(collection(db, "gpt_interactions"), {
        timestamp: new Date().toISOString(),
        input: fullPrompt,
        output: data.choices[0]?.message?.content,
        notes: ""
      });

      setLastDocId(docRef.id);
    } catch (error) {
      console.error("❌ API 请求错误:", error);
      setDesignGuide("API request failed, please check network or API Key.");
    } finally {
      setLoading(false);
    }
  };

  const generateNewDimension = async () => {
    if (!apiKey || !userFeedback) {
      setGeneratedDimension("Please provide feedback first.");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-4o",
          messages: [{ role: "system", content: `User feedback: ${userFeedback}. What additional dimension should be considered?` }],
          max_tokens: 500
        })
      });

      const data = await response.json();
      setGeneratedDimension(data.choices[0]?.message?.content || "Failed to generate.");

    } catch (error) {
      console.error("❌ API 请求错误:", error);
      setGeneratedDimension("Failed to generate dimension.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <button onClick={() => setCurrentGroup("A")} className={currentGroup === "A" ? "active" : ""}>Group A (有专家设计师)</button>
        <button onClick={() => setCurrentGroup("B")} className={currentGroup === "B" ? "active" : ""}>Group B (没有专家设计师)</button>
      </div>

      <div className="container">
        <div className="left-section">
          <h2>Designer Goal</h2>
          <textarea
            placeholder="Enter AI designer goal definition..."
            value={designerGoal}
            onChange={(e) => setDesignerGoal(e.target.value)}
          />
          <h2>User Profile (Multiple Dimensions)</h2>
          {userProfiles.map((profile, index) => (
            <div key={index} className="user-profile-container">
              <input className="user-dimension" value={profile.dimension} onChange={(e) => updateUserProfile(index, "dimension", e.target.value)} />
              <textarea className="user-content" value={profile.content} onChange={(e) => updateUserProfile(index, "content", e.target.value)} />
            </div>
          ))}
          <button className="small-button" onClick={addUserProfile}>Add Dimension</button>
          <button className="full-width generate-button" onClick={callGPT4oAPI} disabled={loading}>
            {loading ? "Generating..." : "Generate Design Guide"}
          </button>
        </div>

        <div className="right-section expanded">
          <h2>Design Guide <button className="copy-button" onClick={copyToClipboard}>Copy</button></h2>
          <textarea className="design-guide-box" readOnly value={designGuide} />

          {currentGroup === "A" ? (
            <>
              <h2>设计专家 Note <button className="copy-button">Save</button></h2>
              <textarea className="notes-box" value={notes} onChange={(e) => setNotes(e.target.value)} />
            </>
          ) : (
            <>
              <h2>User Feedback <button className="copy-button">Save</button></h2>
              <textarea className="notes-box" value={userFeedback} onChange={(e) => setUserFeedback(e.target.value)} />
              <h2>New Dimension <button className="copy-button" onClick={generateNewDimension}>Generate</button></h2>
              <textarea className="notes-box" readOnly value={generatedDimension} />
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
