import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [designerGoal, setDesignerGoal] = useState("");
  const [userProfiles, setUserProfiles] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPrompt, setCustomPrompt] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [designGuide, setDesignGuide] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);
  const [lastDocId, setLastDocId] = useState(null);

  const addUserProfile = () => {
    setUserProfiles([...userProfiles, { dimension: "", content: "" }]);
  };

  const updateUserProfile = (index, field, value) => {
    const updatedProfiles = [...userProfiles];
    updatedProfiles[index][field] = value;
    setUserProfiles(updatedProfiles);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(designGuide);
  };

  return (
    <div className="app-container">
      <h1 className="page-title">Human-AI Collaborative Method for Future-Oriented Design Prompt System</h1>
      <div className="container">
        {/* 左侧输入区 */}
        <div className="left-section">
          <h2>Designer Goal</h2>
          <textarea
            placeholder="Enter AI designer goal definition..."
            value={designerGoal}
            onChange={(e) => setDesignerGoal(e.target.value)}
          />
          <h2>User Profile (Multiple Dimensions)</h2>
          {userProfiles.map((profile, index) => (
            <div key={index} className="user-profile-container">
              <input
                className="user-dimension"
                value={profile.dimension}
                onChange={(e) => updateUserProfile(index, "dimension", e.target.value)}
              />
              <textarea
                className="user-content"
                placeholder={`Enter ${profile.dimension}...`}
                value={profile.content}
                onChange={(e) => updateUserProfile(index, "content", e.target.value)}
              />
            </div>
          ))}
          <button className="small-button" onClick={addUserProfile}>Add Dimension</button>
          <h2>Custom Prompt</h2>
          <textarea
            placeholder="Enter custom Prompt..."
            value={customPrompt}
            onChange={(e) => setCustomPrompt(e.target.value)}
          />
          <h2>OpenAI API Key</h2>
          <input
            type="password"
            placeholder="Enter your OpenAI API Key..."
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
          />
          <button className="full-width generate-button">Generate Design Guide</button>
        </div>

        {/* 右侧输出区 */}
        <div className="right-section expanded">
          <h2 className="guide-header">
            Design Guide <button className="copy-button" onClick={copyToClipboard}>Copy</button>
          </h2>
          <textarea className="design-guide-box"
            readOnly
            value={designGuide}
            placeholder="The design guide will be generated here..."
          />

          <h2 className="notes-header">
            Notes <button className="copy-button">Save</button>
          </h2>
          <textarea
            className="notes-box"
            placeholder="Enter your notes here..."
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
          />
        </div>
      </div>

      <style>
        {`
          /* ✅ 解决背景白色问题 */
          html, body {
            height: 100%;
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
            background: #121212;
            color: #ffffff;
            display: flex;
            justify-content: center;
            align-items: center;
          }

          .page-title {
            text-align: center;
            font-size: 18px;
            margin-top: 10px;
            color: #bb86fc;
          }

          .container {
            display: flex;
            width: 90vw;
            height: 90vh;
            background: #1e1e1e;
            border-radius: 10px;
            overflow: hidden;
          }

          .left-section, .right-section.expanded {
            width: 50%;
            display: flex;
            flex-direction: column;
            padding: 20px;
          }

          .user-profile-container {
            display: flex;
            flex-direction: row;
            gap: 20px;
            margin-bottom: 12px;
          }

          .generate-button, .small-button, .copy-button {
            background: #bb86fc; 
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            padding: 8px 12px;
          }

          .small-button {
            width: 40%;
          }

          .copy-button {
            font-size: 14px;
            padding: 5px 10px;
            background: #bb86fc;
          }

          .design-guide-box {
            flex-grow: 1;
            min-height: 300px; /* ✅ 增加 Design Guide 高度 */
          }

          .notes-box {
            min-height: 80px;
          }

          h2 {
            color: #bb86fc;
          }

          textarea, input {
            padding: 8px;
            border: none;
            border-radius: 5px;
            background: #333;
            color: #fff;
          }
        `}
      </style>
    </div>
  );
}

export default App;
