import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [activeGroup, setActiveGroup] = useState("A"); // Default to Group A

  // Group A (With Design Expert)
  const [designerGoalA, setDesignerGoalA] = useState("");
  const [userProfilesA, setUserProfilesA] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptA, setCustomPromptA] = useState("");
  const [apiKeyA, setApiKeyA] = useState("");
  const [designGuideA, setDesignGuideA] = useState("");
  const [designerNotes, setDesignerNotes] = useState("");
  const [loadingA, setLoadingA] = useState(false);
  const [lastDocIdA, setLastDocIdA] = useState(null);

  // Group B (Without Design Expert)
  const [designerGoalB, setDesignerGoalB] = useState("");
  const [userProfilesB, setUserProfilesB] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptB, setCustomPromptB] = useState("");
  const [apiKeyB, setApiKeyB] = useState("");
  const [designGuideB, setDesignGuideB] = useState("");
  const [userFeedback, setUserFeedback] = useState("");
  const [suggestedDimensions, setSuggestedDimensions] = useState("");
  const [loadingB, setLoadingB] = useState(false);
  const [lastDocIdB, setLastDocIdB] = useState(null);

  // Function to add a new dimension
  const addUserProfile = (group) => {
    if (group === "A") {
      setUserProfilesA([...userProfilesA, { dimension: "", content: "" }]);
    } else {
      setUserProfilesB([...userProfilesB, { dimension: "", content: "" }]);
    }
  };

  // Function to update a user profile
  const updateUserProfile = (group, index, field, value) => {
    if (group === "A") {
      const updatedProfiles = [...userProfilesA];
      updatedProfiles[index][field] = value;
      setUserProfilesA(updatedProfiles);
    } else {
      const updatedProfiles = [...userProfilesB];
      updatedProfiles[index][field] = value;
      setUserProfilesB(updatedProfiles);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  // === Function to Generate Design Guide (for A & B) ===
  const callGPT4oAPI = async (group) => {
    const setLoading = group === "A" ? setLoadingA : setLoadingB;
    const setDesignGuide = group === "A" ? setDesignGuideA : setDesignGuideB;
    const userProfiles = group === "A" ? userProfilesA : userProfilesB;
    const designerGoal = group === "A" ? designerGoalA : designerGoalB;
    const customPrompt = group === "A" ? customPromptA : customPromptB;
    const apiKey = group === "A" ? apiKeyA : apiKeyB;

    if (!apiKey) {
      alert("Please provide OpenAI API Key.");
      return;
    }

    setLoading(true);
    let userProfileText = userProfiles
      .map((profile) => `${profile.dimension}: ${profile.content}`)
      .join("\n\n");

    let fullPrompt = `AI Designer Goal: ${designerGoal}\n\nUser Profiles:\n${userProfileText}\n\nCustom Prompt:\n${customPrompt}`;

    if (group === "B") {
      fullPrompt += `\n\nUser Feedback: ${userFeedback}\n\nSuggest new Additional Dimensions based on this feedback.`;
    }

    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-4o",
          messages: [{ role: "system", content: fullPrompt }],
          max_tokens: 2000,
          temperature: 0.7
        })
      });

      const data = await response.json();
      const outputText = data.choices[0]?.message?.content || "Generation failed, please check API request.";
      setDesignGuide(outputText);

      if (group === "B") {
        setSuggestedDimensions(outputText);
      }

      await addDoc(collection(db, `group${group}Sessions`), {
        timestamp: new Date().toISOString(),
        designerGoal,
        userProfiles,
        customPrompt,
        designGuide: outputText,
        userFeedback: group === "B" ? userFeedback : "",
        suggestedDimensions: group === "B" ? outputText : ""
      });
    } catch (error) {
      console.error("❌ API Request Error:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h1 className="page-title">Human-AI Collaborative Method for Future-Oriented Design Prompt System</h1>

      <div className="layout-container">
        <div className="group-selector">
          <button 
            className={`group-button ${activeGroup === "A" ? "active" : ""}`} 
            onClick={() => setActiveGroup("A")}
          >
            Group A (with Design Expert)
          </button>
          <button 
            className={`group-button ${activeGroup === "B" ? "active" : ""}`} 
            onClick={() => setActiveGroup("B")}
          >
            Group B (without Design Expert)
          </button>
        </div>

        <div className="container">
          {activeGroup === "A" ? renderGroupA() : renderGroupB()}
        </div>
      </div>

      <style>
        {`
          /* ✅ 解决背景白色问题 */
          html, body {
            height: 100%;
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
            background: #121212;
            color: #ffffff;
            display: flex;
            justify-content: center;
            align-items: center;
          }

          .page-title {
            text-align: center;
            font-size: 18px;
            margin-top: 10px;
            color: #bb86fc;
          }

          .layout-container {
            display: flex;
            flex-direction: column;
            width: 95vw;
            height: 95vh;
          }

          .group-selector {
            display: flex;
            justify-content: flex-start;
            margin-bottom: 10px;
          }

          .group-button {
            background: #333;
            color: white;
            border: none;
            border-radius: 5px 5px 0 0;
            cursor: pointer;
            padding: 10px 15px;
            margin-right: 5px;
          }

          .group-button.active {
            background: #bb86fc;
          }

          .container {
            display: flex;
            flex: 1;
            background: #1e1e1e;
            border-radius: 10px;
            overflow: hidden;
          }

          .left-section, .right-section.expanded {
            width: 50%;
            display: flex;
            flex-direction: column;
            padding: 20px;
            overflow-y: auto;
          }

          .user-profile-container {
            display: flex;
            flex-direction: row;
            gap: 20px;
            margin-bottom: 12px;
          }

          .user-dimension {
            width: 40%;
          }

          .user-content {
            width: 60%;
          }

          .generate-button, .small-button, .copy-button, .generate-dimensions-button {
            background: #bb86fc; 
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            padding: 8px 12px;
            margin-bottom: 15px;
          }

          .generate-button:disabled, .generate-dimensions-button:disabled {
            background: #666;
            cursor: not-allowed;
          }

          .small-button {
            width: 40%;
          }

          .copy-button {
            font-size: 14px;
            padding: 5px 10px;
            background: #bb86fc;
          }

          .design-guide-box {
            flex-grow: 1;
            min-height: 300px;
            margin-bottom: 15px;
          }

          .notes-box, .feedback-box, .suggested-dimensions-box {
            min-height: 80px;
            margin-bottom: 15px;
          }

          .feedback-section {
            display: flex;
            flex-direction: column;
          }

          h2 {
            color: #bb86fc;
            margin-bottom: 10px;
          }

          textarea, input {
            padding: 8px;
            border: none;
            border-radius: 5px;
            background: #333;
            color: #fff;
            margin-bottom: 10px;
          }

          input:focus, textarea:focus {
            outline: 1px solid #bb86fc;
          }

          .full-width {
            width: 100%;
          }
        `}
      </style>
    </div>
  );
}

export default App;
