import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [currentGroup, setCurrentGroup] = useState("A"); // A ÁªÑ or B ÁªÑ
  const [designerGoal, setDesignerGoal] = useState("");
  const [userProfiles, setUserProfiles] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPrompt, setCustomPrompt] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [designGuide, setDesignGuide] = useState("");
  const [notes, setNotes] = useState("");
  const [userFeedback, setUserFeedback] = useState(""); // Group B Áî®Êà∑ÂèçÈ¶à
  const [additionalDimensions, setAdditionalDimensions] = useState(""); // ÈúÄË¶ÅË°•ÂÖÖÁöÑÁª¥Â∫¶
  const [loading, setLoading] = useState(false);
  const [lastDocId, setLastDocId] = useState(null);

  const addUserProfile = () => {
    setUserProfiles([...userProfiles, { dimension: "", content: "" }]);
  };

  const updateUserProfile = (index, field, value) => {
    const updatedProfiles = [...userProfiles];
    updatedProfiles[index][field] = value;
    setUserProfiles(updatedProfiles);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(designGuide);
  };

  // üî• Ë∞ÉÁî® GPT-4o ÁîüÊàêËÆæËÆ°ÊåáÂçó
  const callGPT4oAPI = async () => {
    if (!apiKey) {
      setDesignGuide("Please provide OpenAI API Key.");
      return;
    }

    setLoading(true);
    let userProfileText = userProfiles
      .map((profile, index) => `Dimension ${index + 1}: ${profile.dimension}\nContent: ${profile.content}`)
      .join("\n\n");

    let fullPrompt = `AI Designer Goal: ${designerGoal}\n\nUser Profiles:\n${userProfileText}\n\nCustom Prompt:\n${customPrompt}\n\nPlease generate a design guide based on the above information.`;

    if (currentGroup === "B") {
      fullPrompt += `\n\nUser Feedback: ${userFeedback}\n\nGenerate new Additional Dimensions based on this feedback.`;
    }

    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-4o",
          messages: [{ role: "system", content: fullPrompt }],
          max_tokens: 2000,
          temperature: 0.7
        })
      });

      const data = await response.json();
      const outputText = data.choices[0]?.message?.content || "Generation failed, please check API request.";
      setDesignGuide(outputText);

      if (currentGroup === "B") {
        setAdditionalDimensions(outputText); // B ÁªÑ ÁîüÊàêÊñ∞ÁöÑÁª¥Â∫¶
      }

      const docRef = await addDoc(collection(db, "gpt_interactions"), {
        timestamp: new Date().toISOString(),
        input: fullPrompt,
        output: outputText,
        notes: currentGroup === "A" ? notes : userFeedback,
        additionalDimensions: currentGroup === "B" ? outputText : ""
      });

      setLastDocId(docRef.id);
    } catch (error) {
      console.error("‚ùå API ËØ∑Ê±ÇÈîôËØØ:", error);
      setDesignGuide("API request failed, please check network or API Key.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="sidebar">
        <button onClick={() => setCurrentGroup("A")}>Group A (With Expert Designers)</button>
        <button onClick={() => setCurrentGroup("B")}>Group B (Without Expert Designers)</button>
      </div>

      <div className="main-content">
        <h1 className="page-title">Human-AI Collaborative Method for Future-Oriented Design Prompt System</h1>
        <h2>Designer Goal</h2>
        <textarea
          placeholder="Enter AI designer goal definition..."
          value={designerGoal}
          onChange={(e) => setDesignerGoal(e.target.value)}
        />

        <h2>User Profile (Multiple Dimensions)</h2>
        {userProfiles.map((profile, index) => (
          <div key={index} className="user-profile-container">
            <input
              value={profile.dimension}
              onChange={(e) => updateUserProfile(index, "dimension", e.target.value)}
            />
            <textarea
              placeholder={`Enter ${profile.dimension}...`}
              value={profile.content}
              onChange={(e) => updateUserProfile(index, "content", e.target.value)}
            />
          </div>
        ))}
        <button className="small-button" onClick={addUserProfile}>Add Dimension</button>

        <h2>Custom Prompt</h2>
        <textarea placeholder="Enter custom Prompt..." value={customPrompt} onChange={(e) => setCustomPrompt(e.target.value)} />

        <h2>OpenAI API Key</h2>
        <input type="password" placeholder="Enter your OpenAI API Key..." value={apiKey} onChange={(e) => setApiKey(e.target.value)} />

        <button className="full-width generate-button" onClick={callGPT4oAPI} disabled={loading}>{loading ? "Generating..." : "Generate Design Guide"}</button>

        <h2>Design Guide <button className="copy-button" onClick={copyToClipboard}>Copy</button></h2>
        <textarea readOnly value={designGuide} placeholder="The design guide will be generated here..." />

        {currentGroup === "A" ? (
          <>
            <h2>Expert Designer Notes <button className="copy-button">Save</button></h2>
            <textarea placeholder="Enter expert designer notes..." value={notes} onChange={(e) => setNotes(e.target.value)} />
          </>
        ) : (
          <>
            <h2>User Feedback <button className="copy-button">Save</button></h2>
            <textarea placeholder="Enter feedback..." value={userFeedback} onChange={(e) => setUserFeedback(e.target.value)} />

            <h2>Additional Dimensions</h2>
            <textarea readOnly value={additionalDimensions} placeholder="AI-generated dimensions based on feedback..." />
          </>
        )}
      </div>
    </div>
  );
}

export default App;
