import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [activeGroup, setActiveGroup] = useState("A"); // Default to Group A

  // Group A (With Design Expert)
  const [designerGoalA, setDesignerGoalA] = useState("");
  const [userProfilesA, setUserProfilesA] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptA, setCustomPromptA] = useState("");
  const [apiKeyA, setApiKeyA] = useState("");
  const [designGuideA, setDesignGuideA] = useState("");
  const [designerNotes, setDesignerNotes] = useState("");
  const [loadingA, setLoadingA] = useState(false);

  // Group B (Without Design Expert)
  const [designerGoalB, setDesignerGoalB] = useState("");
  const [userProfilesB, setUserProfilesB] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptB, setCustomPromptB] = useState("");
  const [apiKeyB, setApiKeyB] = useState("");
  const [designGuideB, setDesignGuideB] = useState("");
  const [userFeedback, setUserFeedback] = useState("");
  const [suggestedDimensions, setSuggestedDimensions] = useState("");
  const [loadingB, setLoadingB] = useState(false);

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  // **🔥 定义 renderGroupA (修复错误)**
  const renderGroupA = () => (
    <>
      <div className="left-section">
        <h2>Designer Goal</h2>
        <textarea placeholder="Enter AI designer goal definition..." value={designerGoalA} onChange={(e) => setDesignerGoalA(e.target.value)} />
        <h2>User Profile (Multiple Dimensions)</h2>
        {userProfilesA.map((profile, index) => (
          <div key={index} className="user-profile-container">
            <input className="user-dimension" value={profile.dimension} onChange={(e) => {
              const updatedProfiles = [...userProfilesA];
              updatedProfiles[index].dimension = e.target.value;
              setUserProfilesA(updatedProfiles);
            }} />
            <textarea className="user-content" placeholder={`Enter ${profile.dimension}...`} value={profile.content} onChange={(e) => {
              const updatedProfiles = [...userProfilesA];
              updatedProfiles[index].content = e.target.value;
              setUserProfilesA(updatedProfiles);
            }} />
          </div>
        ))}
        <button className="small-button" onClick={() => setUserProfilesA([...userProfilesA, { dimension: "", content: "" }])}>Add Dimension</button>
        <h2>Custom Prompt</h2>
        <textarea placeholder="Enter custom Prompt..." value={customPromptA} onChange={(e) => setCustomPromptA(e.target.value)} />
        <h2>OpenAI API Key</h2>
        <input type="password" placeholder="Enter your OpenAI API Key..." value={apiKeyA} onChange={(e) => setApiKeyA(e.target.value)} />
        <button className="full-width generate-button">Generate Design Guide</button>
      </div>

      <div className="right-section expanded">
        <h2 className="guide-header">
          Design Guide <button className="copy-button" onClick={() => copyToClipboard(designGuideA)}>Copy</button>
        </h2>
        <textarea className="design-guide-box" readOnly value={designGuideA} placeholder="The design guide will be generated here..." />
        <h2 className="notes-header">
          Design Expert Notes <button className="copy-button">Save</button>
        </h2>
        <textarea className="notes-box" placeholder="Enter design expert interview notes here..." value={designerNotes} onChange={(e) => setDesignerNotes(e.target.value)} />
      </div>
    </>
  );

  // **🔥 定义 renderGroupB (修复错误)**
  const renderGroupB = () => (
    <>
      <div className="left-section">
        <h2>Designer Goal</h2>
        <textarea placeholder="Enter AI designer goal definition..." value={designerGoalB} onChange={(e) => setDesignerGoalB(e.target.value)} />
        <h2>User Profile (Multiple Dimensions)</h2>
        {userProfilesB.map((profile, index) => (
          <div key={index} className="user-profile-container">
            <input className="user-dimension" value={profile.dimension} onChange={(e) => {
              const updatedProfiles = [...userProfilesB];
              updatedProfiles[index].dimension = e.target.value;
              setUserProfilesB(updatedProfiles);
            }} />
            <textarea className="user-content" placeholder={`Enter ${profile.dimension}...`} value={profile.content} onChange={(e) => {
              const updatedProfiles = [...userProfilesB];
              updatedProfiles[index].content = e.target.value;
              setUserProfilesB(updatedProfiles);
            }} />
          </div>
        ))}
