            Save
          </button>
        </h2>
        <textarea
          className="notes-box"
          placeholder="Enter expert notes..."
          value={designerNotes}
          onChange={(e) => setDesignerNotes(e.target.value)}
        />
      </div>
    </>
  );

  const renderGroupB = () => (
    <>
      <div className="left-section">
        <h2>Designer Goal</h2>
        <textarea
