          onChange={(e) => setCustomPromptB(e.target.value)}
        />
        <h2>OpenAI API Key</h2>
        <input
          type="password"
          placeholder="Enter API key..."
          value={apiKeyB}
          onChange={(e) => setApiKeyB(e.target.value)}
        />

        <h2>Model Parameters</h2>
        <div className="model-params">
          <div className="param-group">
            <label>Temperature: {temperatureB}</label>
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={temperatureB}
              onChange={(e) => setTemperatureB(parseFloat(e.target.value))}
            />
          </div>

          <div className="param-group">
            <label>Max Tokens: {maxTokensB}</label>
            <input
              type="range"
              min="500"
              max="8000"
              step="500"
              value={maxTokensB}
              onChange={(e) => setMaxTokensB(parseInt(e.target.value))}
