import { useState } from "react";
import { db, collection, addDoc, doc, updateDoc } from "./firebase";

function App() {
  const [activeGroup, setActiveGroup] = useState("A"); // Default to Group A

  // Group A (With Design Expert)
  const [designerGoalA, setDesignerGoalA] = useState("");
  const [userProfilesA, setUserProfilesA] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptA, setCustomPromptA] = useState("");
  const [apiKeyA, setApiKeyA] = useState("");
  const [designGuideA, setDesignGuideA] = useState("");
  const [designerNotes, setDesignerNotes] = useState("");
  const [loadingA, setLoadingA] = useState(false);
  const [lastDocIdA, setLastDocIdA] = useState(null);

  // Group B (Without Design Expert)
  const [designerGoalB, setDesignerGoalB] = useState("");
  const [userProfilesB, setUserProfilesB] = useState([
    { dimension: "User ID", content: "" },
    { dimension: "Age", content: "" },
    { dimension: "Gender", content: "" },
    { dimension: "TAM Group", content: "" },
    { dimension: "Commuting Situation", content: "" }
  ]);
  const [customPromptB, setCustomPromptB] = useState("");
  const [apiKeyB, setApiKeyB] = useState("");
  const [designGuideB, setDesignGuideB] = useState("");
  const [userFeedback, setUserFeedback] = useState("");
  const [suggestedDimensions, setSuggestedDimensions] = useState("");
  const [loadingB, setLoadingB] = useState(false);
  const [lastDocIdB, setLastDocIdB] = useState(null);

  // **🔥 生成设计指南**
  const generateDesignGuide = async (group) => {
    const isGroupA = group === "A";
    const apiKey = isGroupA ? apiKeyA : apiKeyB;
    const designerGoal = isGroupA ? designerGoalA : designerGoalB;
    const userProfiles = isGroupA ? userProfilesA : userProfilesB;
    const customPrompt = isGroupA ? customPromptA : customPromptB;

    if (!apiKey) {
      alert("API Key is required.");
      return;
    }

    isGroupA ? setLoadingA(true) : setLoadingB(true);

    try {
      const profiles = userProfiles
        .filter(p => p.dimension && p.content)
        .map(p => `${p.dimension}: ${p.content}`)
        .join("\n");

      const prompt = `
Designer Goal: ${designerGoal}
User Profile:
${profiles}
${customPrompt ? `Additional Instructions: ${customPrompt}` : ""}
Based on the above information, generate a comprehensive design guide for a future-oriented design solution.
      `.trim();

      // **GPT API 调用**
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-4-turbo",
          messages: [{ role: "user", content: prompt }],
          temperature: 0.7,
          max_tokens: 2000
        })
      });

      const data = await response.json();
      if (data.error) throw new Error(data.error.message || "Error calling OpenAI API");

      const generatedGuide = data.choices[0].message.content;

      if (isGroupA) {
        setDesignGuideA(generatedGuide);
      } else {
        setDesignGuideB(generatedGuide);
      }

      // **🔥 Firebase 存储**
      const sessionData = {
        designerGoal,
        userProfiles,
        customPrompt,
        designGuide: generatedGuide,
        timestamp: new Date().toISOString()
      };

      const dbCollection = collection(db, isGroupA ? "groupASessions" : "groupBSessions");
      let docRef;

      if (isGroupA ? lastDocIdA : lastDocIdB) {
        docRef = doc(db, isGroupA ? "groupASessions" : "groupBSessions", isGroupA ? lastDocIdA : lastDocIdB);
        await updateDoc(docRef, sessionData);
      } else {
        docRef = await addDoc(dbCollection, sessionData);
        if (isGroupA) {
          setLastDocIdA(docRef.id);
        } else {
          setLastDocIdB(docRef.id);
        }
      }
    } catch (error) {
      console.error("Error generating design guide:", error);
    } finally {
      isGroupA ? setLoadingA(false) : setLoadingB(false);
    }
  };

  // **🔥 生成用户反馈的额外维度**
  const generateSuggestedDimensions = async () => {
    if (!userFeedback.trim()) return;

    setLoadingB(true);
    try {
      const prompt = `
The user has provided the following feedback about an AI-generated design guide:
"${userFeedback}"
Based on this feedback, identify 1-3 NEW dimensions of user information that would help generate a better design guide.
Just list the dimension names, one per line. No explanations.
      `.trim();

      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${apiKeyB}`
        },
        body: JSON.stringify({
          model: "gpt-4-turbo",
          messages: [{ role: "user", content: prompt }],
          temperature: 0.7,
          max_tokens: 200
        })
      });

      const data = await response.json();
      if (data.error) throw new Error(data.error.message || "Error calling OpenAI API");

      const dimensions = data.choices[0].message.content;
      setSuggestedDimensions(dimensions);
    } catch (error) {
      console.error("Error generating dimensions:", error);
    } finally {
      setLoadingB(false);
    }
  };

  return (
    <div className="app-container">
      <h1 className="page-title">Human-AI Collaborative Method for Future-Oriented Design Prompt System</h1>
      <div className="layout-container">
        <div className="group-selector">
          <button className={`group-button ${activeGroup === "A" ? "active" : ""}`} onClick={() => setActiveGroup("A")}>
            Group A (with Design Expert)
          </button>
          <button className={`group-button ${activeGroup === "B" ? "active" : ""}`} onClick={() => setActiveGroup("B")}>
            Group B (without Design Expert)
          </button>
        </div>
        <div className="container">
          {activeGroup === "A" ? (
            <div>
              <button className="full-width generate-button" onClick={() => generateDesignGuide("A")} disabled={loadingA}>
                {loadingA ? "Generating..." : "Generate Design Guide"}
              </button>
              <textarea readOnly value={designGuideA} placeholder="The design guide will be generated here..." />
            </div>
          ) : (
            <div>
              <button className="full-width generate-button" onClick={() => generateDesignGuide("B")} disabled={loadingB}>
                {loadingB ? "Generating..." : "Generate Design Guide"}
              </button>
              <textarea readOnly value={designGuideB} placeholder="The design guide will be generated here..." />
              <h2>User Feedback</h2>
              <textarea placeholder="Enter your feedback..." value={userFeedback} onChange={(e) => setUserFeedback(e.target.value)} />
              <button className="generate-dimensions-button" onClick={generateSuggestedDimensions} disabled={loadingB}>
                {loadingB ? "Generating..." : "Generate New Dimensions"}
              </button>
              <textarea readOnly value={suggestedDimensions} placeholder="Suggested Additional Dimensions will appear here..." />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
